'use strict';
module.exports = (sequelize, DataTypes) => {
  const book = sequelize.define('books', {
    image: {
      allowNull: false,
      type:DataTypes.STRING},
    title: {
      allowNull: false,
      type:DataTypes.STRING},
    status: {
      allowNull: false,
      type: DataTypes.ENUM},
    tacgia_id: {
      allowNull: false,
      type: DataTypes.INTEGER},
    nhacc_id: {
      allowNull: false,
      type :DataTypes.INTEGER
    },
    createdAt: {
      allowNull: false,
      type: DataTypes.DATE
    },
    updatedAt: {
      allowNull: false,
      type: DataTypes.DATE
    }
  }, {});
  book.associate = function(models) {
    // associations can be defined here
    book.hasMany(models.loaibook)
    book.hasMany(models.author)
  };
  return book;
};

