  let uniqueUser = `unique_user_`
  client.keys(`*${uniqueUser}*`, function (err, keys) {
    keys.forEach(function (key) {
      client.del(key)
    });
  });
  return true
}

export const flushCache = () => {
  clientRedis.flushdb()
}

export default { get, set, remove, genKey, genKeyPublic, flushByUser, flushCache }

